INSERT INTO public.sat_questions (domain, question_type, difficulty, question_text, choices, correct_answer, explanation, tags) VALUES
('Craft & Structure', 'mcq', 'easy', 'The scientists research was meticulous, involving careful attention to every detail. As used in this context, meticulous most nearly means:', '["careless", "thorough", "rapid", "expensive"]', 'thorough', 'Meticulous means showing great attention to detail, which is synonymous with thorough.', ARRAY['vocabulary', 'context-clues']),
('Craft & Structure', 'mcq', 'medium', 'The policy had a profound effect on the economy, fundamentally changing how businesses operated. As used here, profound most nearly means:', '["superficial", "deep", "quick", "uncertain"]', 'deep', 'Profound means having deep significance or fundamental impact, as indicated by fundamentally changing.', ARRAY['vocabulary', 'impact']),
('Craft & Structure', 'mcq', 'hard', 'The authors tone was sardonic, using bitter humor to critique social conventions. Which word best captures this meaning?', '["sincere", "mocking", "cheerful", "neutral"]', 'mocking', 'Sardonic refers to bitter, scornful humor used to criticize - best captured by mocking.', ARRAY['vocabulary', 'tone']),
('Craft & Structure', 'mcq', 'easy', 'The data was ambiguous, leading researchers to different interpretations. As used here, ambiguous most nearly means:', '["clear", "uncertain", "accurate", "false"]', 'uncertain', 'Ambiguous means open to multiple interpretations or unclear, leading to different conclusions.', ARRAY['vocabulary', 'clarity']),
('Craft & Structure', 'mcq', 'medium', 'Her argument was compelling, persuading many previously skeptical listeners. In this context, compelling most nearly means:', '["boring", "persuasive", "complicated", "brief"]', 'persuasive', 'Compelling means powerfully persuasive or convincing, as shown by persuading skeptics.', ARRAY['vocabulary', 'persuasion']),
('Craft & Structure', 'mcq', 'hard', 'The politicians speech was incendiary, provoking strong reactions and heated debate. As used here, incendiary most nearly means:', '["calming", "inflammatory", "boring", "factual"]', 'inflammatory', 'Incendiary means inflammatory or provocative, designed to arouse strong feelings, as indicated by heated debate.', ARRAY['vocabulary', 'effect']),
('Craft & Structure', 'mcq', 'easy', 'The solution proved viable, successfully addressing the problem in practical terms. Viable most nearly means:', '["impossible", "workable", "theoretical", "expensive"]', 'workable', 'Viable means capable of working successfully or practically feasible.', ARRAY['vocabulary', 'practicality']),
('Craft & Structure', 'mcq', 'medium', 'The ecosystem was described as resilient, recovering quickly from disturbances. In this context, resilient most nearly means:', '["fragile", "able to recover", "unchanging", "artificial"]', 'able to recover', 'Resilient means able to recover from or adjust to change or hardship, as shown by recovering quickly.', ARRAY['vocabulary', 'adaptation']),
('Craft & Structure', 'mcq', 'hard', 'The artists style was iconoclastic, deliberately breaking from traditional conventions. Iconoclastic most nearly means:', '["conventional", "tradition-challenging", "popular", "careful"]', 'tradition-challenging', 'Iconoclastic means attacking or challenging established beliefs or conventions.', ARRAY['vocabulary', 'innovation']),
('Craft & Structure', 'mcq', 'medium', 'The finding was anomalous, deviating from expected patterns and surprising researchers. Anomalous most nearly means:', '["typical", "unusual", "predicted", "false"]', 'unusual', 'Anomalous means deviating from what is standard or expected - synonymous with unusual.', ARRAY['vocabulary', 'patterns']),
('Craft & Structure', 'mcq', 'easy', 'The instructions were explicit, leaving no room for confusion. As used here, explicit most nearly means:', '["vague", "clearly stated", "complicated", "unwritten"]', 'clearly stated', 'Explicit means stated clearly and in detail, leaving no ambiguity.', ARRAY['vocabulary', 'clarity']),
('Craft & Structure', 'mcq', 'hard', 'The theory was nebulous, with concepts that remained poorly defined. Nebulous most nearly means:', '["precise", "vague", "revolutionary", "proven"]', 'vague', 'Nebulous means unclear, vague, or ill-defined, as indicated by poorly defined.', ARRAY['vocabulary', 'precision']),
('Craft & Structure', 'mcq', 'medium', 'The project required copious amounts of data for analysis. Copious most nearly means:', '["minimal", "abundant", "precise", "digital"]', 'abundant', 'Copious means abundant in supply or quantity - large amounts.', ARRAY['vocabulary', 'quantity']),
('Craft & Structure', 'mcq', 'easy', 'The witness provided credible testimony that aligned with physical evidence. Credible most nearly means:', '["unbelievable", "believable", "lengthy", "emotional"]', 'believable', 'Credible means believable or worthy of trust, as shown by alignment with evidence.', ARRAY['vocabulary', 'trustworthiness']),
('Craft & Structure', 'mcq', 'hard', 'The researchers findings were corroborated by three independent studies. Corroborated most nearly means:', '["contradicted", "confirmed", "ignored", "funded"]', 'confirmed', 'Corroborated means confirmed or supported with evidence, as shown by independent studies agreeing.', ARRAY['vocabulary', 'verification']),
('Craft & Structure', 'mcq', 'medium', 'The substance was volatile, changing states rapidly under various conditions. Volatile most nearly means:', '["stable", "unstable", "solid", "invisible"]', 'unstable', 'Volatile means unstable and liable to change rapidly, as indicated by changing states rapidly.', ARRAY['vocabulary', 'stability']),
('Craft & Structure', 'mcq', 'easy', 'The candidates platform was pragmatic, focusing on achievable goals. Pragmatic most nearly means:', '["idealistic", "practical", "aggressive", "unclear"]', 'practical', 'Pragmatic means practical and realistic, focused on what can actually be accomplished.', ARRAY['vocabulary', 'approach']),
('Craft & Structure', 'mcq', 'hard', 'The novels ending was cathartic, providing emotional release for readers. Cathartic most nearly means:', '["confusing", "cleansing", "disappointing", "predictable"]', 'cleansing', 'Cathartic means providing psychological relief through emotional release or purgation.', ARRAY['vocabulary', 'emotion']),
('Craft & Structure', 'mcq', 'medium', 'The climates effects on agriculture were multifaceted, involving temperature, precipitation, and seasonal patterns. Multifaceted most nearly means:', '["simple", "complex", "minor", "agricultural"]', 'complex', 'Multifaceted means having many aspects or features, involving multiple factors - complex.', ARRAY['vocabulary', 'complexity']),
('Craft & Structure', 'mcq', 'medium', 'The discovery had ramifications across multiple scientific fields. Ramifications most nearly means:', '["origins", "consequences", "experiments", "funding"]', 'consequences', 'Ramifications means consequences or implications, especially complex ones that spread across areas.', ARRAY['vocabulary', 'effects']),
('Craft & Structure', 'mcq', 'easy', 'The communities response was unanimous, with everyone agreeing on the action plan. Unanimous most nearly means:', '["divided", "in complete agreement", "hasty", "delayed"]', 'in complete agreement', 'Unanimous means fully in agreement, with no dissenting opinions.', ARRAY['vocabulary', 'agreement']),
('Craft & Structure', 'mcq', 'medium', 'The historians account was meticulous, documenting every detail of the events. Meticulous most nearly means:', '["careless", "extremely careful", "brief", "biased"]', 'extremely careful', 'Meticulous means showing extreme care and attention to detail.', ARRAY['vocabulary', 'precision']),
('Craft & Structure', 'mcq', 'hard', 'The politicians rhetoric was demagogic, appealing to emotions rather than reason. Demagogic most nearly means:', '["rational", "manipulatively emotional", "honest", "technical"]', 'manipulatively emotional', 'Demagogic means appealing to emotions and prejudices rather than using rational argument.', ARRAY['vocabulary', 'manipulation']),
('Craft & Structure', 'mcq', 'easy', 'The treatment was efficacious, successfully curing the disease. Efficacious most nearly means:', '["ineffective", "effective", "expensive", "experimental"]', 'effective', 'Efficacious means successful in producing the desired result.', ARRAY['vocabulary', 'effectiveness']),
('Craft & Structure', 'mcq', 'medium', 'The leaders decision was arbitrary, made without clear reasoning. Arbitrary most nearly means:', '["well-reasoned", "random", "democratic", "delayed"]', 'random', 'Arbitrary means based on random choice or personal whim rather than reason or system.', ARRAY['vocabulary', 'reasoning']),
('Craft & Structure', 'mcq', 'hard', 'The authors prose was turgid, overly complex and difficult to understand. Turgid most nearly means:', '["clear", "pompously inflated", "brief", "poetic"]', 'pompously inflated', 'Turgid means excessively ornate or complex in style, often pompous.', ARRAY['vocabulary', 'style']),
('Craft & Structure', 'mcq', 'easy', 'The evidence was conclusive, proving the hypothesis beyond doubt. Conclusive most nearly means:', '["uncertain", "definitive", "preliminary", "disputed"]', 'definitive', 'Conclusive means serving to settle an issue decisively.', ARRAY['vocabulary', 'certainty']),
('Craft & Structure', 'mcq', 'medium', 'The scientists approach was empirical, based on observation rather than theory. Empirical most nearly means:', '["theoretical", "observation-based", "mathematical", "speculative"]', 'observation-based', 'Empirical means based on observation or experience rather than theory.', ARRAY['vocabulary', 'methodology']),
('Craft & Structure', 'mcq', 'hard', 'The speakers manner was obsequious, excessively eager to please. Obsequious most nearly means:', '["independent", "servilely compliant", "aggressive", "confident"]', 'servilely compliant', 'Obsequious means obedient or attentive to an excessive degree.', ARRAY['vocabulary', 'behavior']),
('Craft & Structure', 'mcq', 'medium', 'The historians perspective was anachronistic, applying modern values to past events. Anachronistic most nearly means:', '["timely", "chronologically misplaced", "accurate", "objective"]', 'chronologically misplaced', 'Anachronistic means belonging to a period other than that being portrayed.', ARRAY['vocabulary', 'time']),
('Craft & Structure', 'mcq', 'easy', 'The argument was fallacious, containing logical errors. Fallacious most nearly means:', '["sound", "flawed", "lengthy", "persuasive"]', 'flawed', 'Fallacious means based on a mistaken belief or containing logical flaws.', ARRAY['vocabulary', 'logic']),
('Craft & Structure', 'mcq', 'hard', 'The witness gave perfunctory testimony, showing little genuine interest. Perfunctory most nearly means:', '["thorough", "superficially done", "enthusiastic", "detailed"]', 'superficially done', 'Perfunctory means carried out with minimal effort or care.', ARRAY['vocabulary', 'effort']),
('Craft & Structure', 'mcq', 'medium', 'The researchers findings were provisional, subject to further confirmation. Provisional most nearly means:', '["final", "temporary", "rejected", "secret"]', 'temporary', 'Provisional means arranged or existing for the present, possibly to be changed.', ARRAY['vocabulary', 'status']),
('Craft & Structure', 'mcq', 'easy', 'The speakers words were concise, brief but comprehensive. Concise most nearly means:', '["wordy", "brief and clear", "vague", "lengthy"]', 'brief and clear', 'Concise means giving information clearly in few words.', ARRAY['vocabulary', 'brevity']),
('Craft & Structure', 'mcq', 'hard', 'The organizations practices were opaque, lacking transparency. Opaque most nearly means:', '["transparent", "unclear", "efficient", "ethical"]', 'unclear', 'Opaque means not transparent or translucent, difficult to understand.', ARRAY['vocabulary', 'transparency']),
('Craft & Structure', 'mcq', 'medium', 'The critics review was scathing, severely critical of the performance. Scathing most nearly means:', '["praising", "harshly critical", "balanced", "brief"]', 'harshly critical', 'Scathing means witheringly scornful or severely critical.', ARRAY['vocabulary', 'criticism']),
('Craft & Structure', 'mcq', 'easy', 'The solution was innovative, introducing new ideas. Innovative most nearly means:', '["traditional", "creative", "expensive", "complex"]', 'creative', 'Innovative means introducing new ideas or methods.', ARRAY['vocabulary', 'creativity']),
('Craft & Structure', 'mcq', 'hard', 'The politicians statement was equivocal, deliberately ambiguous. Equivocal most nearly means:', '["clear", "intentionally unclear", "honest", "brief"]', 'intentionally unclear', 'Equivocal means open to more than one interpretation, intentionally ambiguous.', ARRAY['vocabulary', 'ambiguity']),
('Craft & Structure', 'mcq', 'medium', 'The researchers method was systematic, following an organized procedure. Systematic most nearly means:', '["random", "methodical", "spontaneous", "complex"]', 'methodical', 'Systematic means done according to a fixed plan or system.', ARRAY['vocabulary', 'organization']),
('Craft & Structure', 'mcq', 'easy', 'The evidence was tangible, able to be touched or measured. Tangible most nearly means:', '["abstract", "concrete", "irrelevant", "disputed"]', 'concrete', 'Tangible means perceptible by touch or clearly real.', ARRAY['vocabulary', 'reality']);