INSERT INTO public.sat_questions (domain, question_type, difficulty, question_text, choices, correct_answer, explanation, tags) VALUES
('Standard English Conventions', 'mcq', 'easy', 'The students forgot ____ textbooks at home.', '["their", "there", "theyre", "theyve"]', 'their', 'Their is the possessive form indicating the textbooks belong to the students.', ARRAY['grammar', 'homophones']),
('Standard English Conventions', 'mcq', 'medium', 'Neither the teachers nor the principal ____ aware of the schedule change.', '["was", "were", "is", "are"]', 'was', 'With neither/nor, the verb agrees with the closer subject (principal), which is singular.', ARRAY['subject-verb-agreement', 'correlative-conjunctions']),
('Standard English Conventions', 'mcq', 'hard', 'The committee ____ its decision after hours of deliberation.', '["reaches", "reach", "have reached", "are reaching"]', 'reaches', 'Committee is a collective noun treated as singular when acting as one unit.', ARRAY['subject-verb-agreement', 'collective-nouns']),
('Standard English Conventions', 'mcq', 'easy', 'She walked ____ the library to return the books.', '["to", "too", "two", "tow"]', 'to', 'To indicates direction or movement toward a destination.', ARRAY['homophones', 'prepositions']),
('Standard English Conventions', 'mcq', 'medium', 'Having finished her homework, Maria ____ to the park.', '["goes", "went", "had gone", "will go"]', 'went', 'Past tense went is consistent with the completed action indicated by having finished.', ARRAY['verb-tense', 'consistency']),
('Standard English Conventions', 'mcq', 'hard', 'The data ____ conclusive; further studies ____ needed.', '["is, are", "are, is", "is, is", "are, are"]', 'is, are', 'Data can be singular (as a mass noun) or plural; here treated as singular. Studies is plural.', ARRAY['subject-verb-agreement', 'plural-ambiguity']),
('Standard English Conventions', 'mcq', 'easy', 'Your performance was better than ____.', '["mine", "me", "my", "mines"]', 'mine', 'Mine is the possessive pronoun comparing performances.', ARRAY['pronouns', 'possessives']),
('Standard English Conventions', 'mcq', 'medium', 'The researchers discovered that the hypothesis ____ by previous data.', '["was supported", "were supported", "is supporting", "supported"]', 'was supported', 'Past passive voice matches the past tense of discovered.', ARRAY['verb-tense', 'passive-voice']),
('Standard English Conventions', 'mcq', 'hard', '____ you or Sarah attending the conference next week?', '["Is", "Are", "Was", "Were"]', 'Are', 'With or, the verb agrees with the closer subject (Sarah), but compound subjects with or typically use plural when both are possible attendees.', ARRAY['subject-verb-agreement', 'compound-subjects']),
('Standard English Conventions', 'mcq', 'easy', '____ going to rain later this afternoon.', '["Its", "Its", "Itll", "Their"]', 'Its', 'Its is the contraction of it is.', ARRAY['contractions', 'homophones']),
('Standard English Conventions', 'mcq', 'medium', 'Each of the participants ____ required to sign a consent form.', '["is", "are", "were", "have been"]', 'is', 'Each is always singular, so the verb must be singular (is).', ARRAY['subject-verb-agreement', 'indefinite-pronouns']),
('Standard English Conventions', 'mcq', 'hard', 'Beside the old oak tree ____ a small pond and several flower beds.', '["sits", "sit", "is sitting", "are sitting"]', 'sit', 'The plural compound subject (pond and beds) requires a plural verb.', ARRAY['subject-verb-agreement', 'inverted-sentences']),
('Standard English Conventions', 'mcq', 'easy', 'The dog wagged ____ tail happily.', '["its", "its", "it", "their"]', 'its', 'Its is the possessive form of it (no apostrophe).', ARRAY['possessives', 'pronouns']),
('Standard English Conventions', 'mcq', 'medium', 'By the time you arrive, I ____ the report.', '["will finish", "will have finished", "am finishing", "finished"]', 'will have finished', 'Future perfect tense indicates an action completed before a future point.', ARRAY['verb-tense', 'future-perfect']),
('Standard English Conventions', 'mcq', 'hard', 'The phenomenon ____ scientists puzzled for decades.', '["has kept", "have kept", "was keeping", "keeps"]', 'has kept', 'Present perfect tense (has kept) indicates an action starting in the past and continuing to the present.', ARRAY['verb-tense', 'present-perfect']),
('Standard English Conventions', 'mcq', 'easy', 'We need to buy ____ apples for the recipe.', '["more", "much", "many", "most"]', 'more', 'More is used for comparisons and works with countable nouns like apples.', ARRAY['adjectives', 'comparatives']),
('Standard English Conventions', 'mcq', 'medium', 'Everyone must bring ____ own laptop to the workshop.', '["their", "his or her", "his", "its"]', 'his or her', 'Formally, everyone is singular and requires his or her, though their is increasingly accepted.', ARRAY['pronoun-agreement', 'singular-they']),
('Standard English Conventions', 'mcq', 'hard', 'The reason for the delay was ____ the equipment malfunctioned.', '["that", "because", "due to", "since"]', 'that', 'After reason was, use that to introduce the noun clause, not because.', ARRAY['idioms', 'clause-structure']),
('Standard English Conventions', 'mcq', 'easy', 'She runs ____ than her brother.', '["faster", "more fast", "fastest", "more faster"]', 'faster', 'Faster is the correct comparative form of fast.', ARRAY['adjectives', 'comparatives']),
('Standard English Conventions', 'mcq', 'medium', 'If I ____ you, I would accept the offer.', '["was", "were", "am", "be"]', 'were', 'Subjunctive mood uses were for hypothetical or contrary-to-fact conditions.', ARRAY['subjunctive-mood', 'conditionals']),
('Standard English Conventions', 'mcq', 'hard', 'The painting, along with several sculptures, ____ at the auction.', '["was sold", "were sold", "is selling", "are selling"]', 'was sold', 'The subject is painting (singular); along with phrases do not affect verb agreement.', ARRAY['subject-verb-agreement', 'intervening-phrases']),
('Standard English Conventions', 'mcq', 'easy', 'Between you and ____, I think she made the right choice.', '["me", "I", "myself", "mine"]', 'me', 'Object pronoun me is correct after the preposition between.', ARRAY['pronouns', 'objective-case']),
('Standard English Conventions', 'mcq', 'medium', 'The number of applicants ____ decreased this year.', '["has", "have", "are", "were"]', 'has', 'The number (singular) requires a singular verb (has).', ARRAY['subject-verb-agreement', 'number-vs-numbers']),
('Standard English Conventions', 'mcq', 'hard', 'Scarcely ____ begun when the fire alarm sounded.', '["had the meeting", "the meeting had", "has the meeting", "the meeting has"]', 'had the meeting', 'Negative adverbs like scarcely require inversion (had the meeting).', ARRAY['syntax', 'inversion']),
('Standard English Conventions', 'mcq', 'easy', 'Please pass the salt ____.', '["to me", "to I", "for me", "at me"]', 'to me', 'To me is the correct prepositional phrase using the object pronoun.', ARRAY['pronouns', 'prepositions']),
('Standard English Conventions', 'mcq', 'medium', 'The effects of pollution ____ becoming increasingly apparent.', '["is", "are", "was", "has been"]', 'are', 'Effects is plural, requiring the plural verb are.', ARRAY['subject-verb-agreement', 'plurals']),
('Standard English Conventions', 'mcq', 'hard', 'Not only ____ excellent grades, but she also excels in sports.', '["does she earn", "she earns", "she does earn", "earned she"]', 'does she earn', 'Not only requires inversion of subject and auxiliary verb.', ARRAY['syntax', 'inversion']),
('Standard English Conventions', 'mcq', 'easy', 'This is the ____ book I have ever read.', '["goodest", "better", "best", "more good"]', 'best', 'Best is the superlative form of good.', ARRAY['adjectives', 'superlatives']),
('Standard English Conventions', 'mcq', 'medium', 'The jury ____ unable to reach a verdict.', '["was", "were", "is being", "are being"]', 'was', 'Jury as a collective unit acting together is singular (was).', ARRAY['subject-verb-agreement', 'collective-nouns']),
('Standard English Conventions', 'mcq', 'hard', '____ the difficulties, the team completed the project on time.', '["Despite", "Despite of", "In spite", "Although"]', 'Despite', 'Despite (without of) is the correct preposition.', ARRAY['prepositions', 'idioms']),
('Standard English Conventions', 'mcq', 'easy', 'The cat is hiding ____ the bed.', '["under", "on", "at", "between"]', 'under', 'Under indicates position below something.', ARRAY['prepositions', 'spatial-relationships']),
('Standard English Conventions', 'mcq', 'medium', 'Either the manager or the employees ____ responsible for the error.', '["is", "are", "was", "been"]', 'are', 'With either/or, the verb agrees with the closer subject (employees, plural).', ARRAY['subject-verb-agreement', 'either-or']),
('Standard English Conventions', 'mcq', 'hard', 'The politician, together with her advisors, ____ the proposal.', '["supports", "support", "is supporting", "are supporting"]', 'supports', 'The subject is politician (singular); together with is a parenthetical phrase.', ARRAY['subject-verb-agreement', 'parenthetical-phrases']),
('Standard English Conventions', 'mcq', 'easy', 'I ____ to the store yesterday.', '["went", "go", "gone", "going"]', 'went', 'Went is the simple past tense of go.', ARRAY['irregular-verbs', 'past-tense']),
('Standard English Conventions', 'mcq', 'medium', 'The students had ____ their assignments before class.', '["completed", "complete", "completing", "completes"]', 'completed', 'Past perfect tense uses had + past participle (completed).', ARRAY['verb-tense', 'past-perfect']),
('Standard English Conventions', 'mcq', 'hard', 'Were it not for your help, we ____ the deadline.', '["would miss", "would have missed", "will miss", "had missed"]', 'would have missed', 'Inverted subjunctive with were requires conditional perfect (would have missed).', ARRAY['subjunctive-mood', 'conditionals']),
('Standard English Conventions', 'mcq', 'easy', 'She is ____ intelligent student in the class.', '["a", "an", "the", "one"]', 'an', 'An is used before vowel sounds.', ARRAY['articles', 'vowel-sounds']),
('Standard English Conventions', 'mcq', 'medium', 'The manager suggested that he ____ the meeting.', '["attends", "attend", "attended", "will attend"]', 'attend', 'Subjunctive mood after suggest requires base form (attend).', ARRAY['subjunctive-mood', 'suggestions']),
('Standard English Conventions', 'mcq', 'hard', 'So quickly ____ that no one noticed his departure.', '["did he leave", "he left", "he did leave", "left he"]', 'did he leave', 'So + adverb requires inversion (did he leave).', ARRAY['syntax', 'inversion']),
