INSERT INTO curriculum_questions (id, topic_id, question, marks, model_answer, marking_criteria, difficulty, spec_reference, calculator_guidance, order_index) VALUES
('sat-cs-q1', 'sat-cs-tone-attitude', 'Write a sentence that shows an excited tone about a new idea.', 2, 'I can''t wait to explore this groundbreaking concept that could change everything!', '{"answer": "A sentence expressing enthusiasm and energy about a new concept", "explanation": "Should use exclamation marks, positive vocabulary, and convey genuine excitement"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 1),
('sat-cs-q2', 'sat-cs-tone-attitude', 'Write a sentence that shows a disappointed tone about a result.', 2, 'Unfortunately, the experiment yielded results far below our expectations.', '{"answer": "A sentence expressing disappointment or letdown", "explanation": "Should use words like unfortunately, sadly, or phrases indicating unmet expectations"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 2),
('sat-cs-q3', 'sat-cs-tone-attitude', 'Write a sentence that uses a neutral and factual tone.', 2, 'The study included 200 participants over a six-month period.', '{"answer": "A sentence stating facts without emotion or opinion", "explanation": "Should be objective, clear, and free from emotional language"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 3),
('sat-cs-q4', 'sat-cs-tone-attitude', 'Write a sentence comparing two things using a compare-and-contrast structure.', 3, 'While both cities offer rich cultural experiences, New York is more fast-paced whereas Paris is more relaxed.', '{"answer": "A sentence showing similarities and differences between two items", "explanation": "Should use comparison words like while, whereas, both, or however"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 4),
('sat-cs-q5', 'sat-cs-tone-attitude', 'Write a sentence explaining why something happened using cause and effect.', 3, 'Because the temperature dropped below freezing, the water pipes burst overnight.', '{"answer": "A sentence showing a cause leading to an effect", "explanation": "Should use words like because, therefore, as a result, or consequently"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 5),
('sat-cs-q6', 'sat-cs-tone-attitude', 'Write a sentence telling steps in order using a sequence structure.', 3, 'First, gather all ingredients, then mix the dry components, and finally add the wet ingredients gradually.', '{"answer": "A sentence showing steps in chronological order", "explanation": "Should use sequence words like first, then, next, finally"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 6),
('sat-cs-q7', 'sat-cs-tone-attitude', 'Write a sentence that shows the writer is uncertain about an outcome.', 2, 'It remains unclear whether the new policy will achieve its intended goals.', '{"answer": "A sentence expressing doubt or lack of certainty", "explanation": "Should use words like unclear, uncertain, perhaps, possibly, or may"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 7),
('sat-cs-q8', 'sat-cs-tone-attitude', 'Write a sentence that shows the writer is confident about a solution.', 2, 'This approach will undoubtedly resolve the issues we have been facing.', '{"answer": "A sentence expressing certainty and assurance", "explanation": "Should use words like will, certainly, undoubtedly, or definitely"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 8),
('sat-cs-q9', 'sat-cs-tone-attitude', 'Write a sentence that presents a problem clearly.', 2, 'The city faces severe traffic congestion that delays commuters by hours each day.', '{"answer": "A sentence clearly stating an issue or challenge", "explanation": "Should identify the problem specifically and its impact"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 9),
('sat-cs-q10', 'sat-cs-tone-attitude', 'Write a sentence that presents a solution to a problem you mentioned.', 2, 'Implementing a comprehensive public transit system could significantly reduce traffic congestion.', '{"answer": "A sentence offering a way to address a problem", "explanation": "Should clearly state a solution and how it addresses the issue"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 10),
('sat-cs-q11', 'sat-cs-tone-attitude', 'Write a sentence that includes evidence or a fact to support a claim.', 3, 'Studies show that regular exercise reduces the risk of heart disease by 30 percent.', '{"answer": "A sentence with data, statistics, or factual information", "explanation": "Should include specific evidence like numbers, research findings, or concrete facts"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 11),
('sat-cs-q12', 'sat-cs-tone-attitude', 'Write a sentence that shows a contrast using a connecting word like "however" or "but".', 2, 'The plan was well-designed; however, the execution proved challenging.', '{"answer": "A sentence showing opposition or difference between two ideas", "explanation": "Should use contrast words like however, but, yet, or nevertheless"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 12),
('sat-cs-q13', 'sat-cs-tone-attitude', 'Write a sentence that shows the writer is skeptical about an idea.', 2, 'The proposal sounds promising, but I question whether it can be implemented effectively.', '{"answer": "A sentence expressing doubt or questioning validity", "explanation": "Should convey reservation or doubt using words like question, doubt, or skeptical"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 13),
('sat-cs-q14', 'sat-cs-tone-attitude', 'Write a sentence that shows the writer is supportive of a proposal.', 2, 'This initiative deserves our full support as it addresses a critical community need.', '{"answer": "A sentence expressing approval or endorsement", "explanation": "Should use positive language showing agreement or backing"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 14),
('sat-cs-q15', 'sat-cs-tone-attitude', 'Write a sentence that shows the writer is warning about a risk or danger.', 2, 'Failing to address climate change now could lead to irreversible environmental damage.', '{"answer": "A sentence cautioning about potential negative consequences", "explanation": "Should convey urgency and identify specific risks"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 15),
('sat-cs-q16', 'sat-cs-tone-attitude', 'Write a sentence that clearly summarizes an idea in one line.', 2, 'In essence, the research confirms that early intervention improves long-term outcomes.', '{"answer": "A brief sentence capturing the main point", "explanation": "Should be concise and capture the core message"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 16),
('sat-cs-q17', 'sat-cs-tone-attitude', 'Write a sentence that shows the writer is surprised by a result.', 2, 'Surprisingly, the data revealed patterns completely opposite to our predictions.', '{"answer": "A sentence expressing unexpected findings", "explanation": "Should use words like surprisingly, unexpectedly, or remarkably"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 17),
('sat-cs-q18', 'sat-cs-tone-attitude', 'Write a sentence that includes a counterargument to an opinion.', 3, 'While some argue that technology isolates people, others contend it actually enhances connectivity.', '{"answer": "A sentence presenting an opposing viewpoint", "explanation": "Should acknowledge an alternative perspective using words like while, although, or others argue"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 18),
('sat-cs-q19', 'sat-cs-tone-attitude', 'Write a sentence that includes a concession, such as "Although this may be true…".', 3, 'Although the method is time-consuming, it produces the most accurate results.', '{"answer": "A sentence acknowledging a point before presenting another", "explanation": "Should use concession words like although, while, even though"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 19),
('sat-cs-q20', 'sat-cs-tone-attitude', 'Write a sentence that clarifies information using a phrase like "In other words…".', 2, 'The process is iterative; in other words, it repeats until the desired outcome is achieved.', '{"answer": "A sentence restating something in simpler terms", "explanation": "Should use clarifying phrases like in other words, that is, or to put it simply"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 20),
('sat-cs-q21', 'sat-cs-tone-attitude', 'Write a sentence that shows the writer is evaluating or judging something.', 2, 'The new policy represents a significant improvement over previous approaches.', '{"answer": "A sentence making an assessment or judgment", "explanation": "Should express evaluation using words like effective, successful, or improvement"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 21),
('sat-cs-q22', 'sat-cs-tone-attitude', 'Write a sentence that shows a cause and then a result.', 2, 'The prolonged drought caused widespread crop failures across the region.', '{"answer": "A sentence with a cause leading to an effect", "explanation": "Should clearly show causation with words like caused, led to, or resulted in"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 22),
('sat-cs-q23', 'sat-cs-tone-attitude', 'Write a sentence that clearly states the purpose of something.', 2, 'The purpose of this program is to provide accessible healthcare to underserved communities.', '{"answer": "A sentence explaining why something exists or what it aims to achieve", "explanation": "Should explicitly state the goal or intention"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 23),
('sat-cs-q24', 'sat-cs-tone-attitude', 'Write a sentence that gives a comparison using more/less or better/worse.', 2, 'This method is more efficient and less costly than traditional approaches.', '{"answer": "A sentence comparing using comparative adjectives", "explanation": "Should use comparative forms like more, less, better, worse"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 24),
('sat-cs-q25', 'sat-cs-tone-attitude', 'Write a sentence that expresses a strong opinion clearly.', 2, 'Renewable energy is absolutely essential for our planet''s future survival.', '{"answer": "A sentence stating a firm belief or position", "explanation": "Should use strong language like must, essential, critical, or absolutely"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 25),
('sat-cs-q26', 'sat-cs-tone-attitude', 'Write a sentence that shows a change of perspective (example: "At first… Now…").', 3, 'At first, I was skeptical of the plan, but now I recognize its potential benefits.', '{"answer": "A sentence showing evolution of thought over time", "explanation": "Should use time markers showing before and after perspectives"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 26),
('sat-cs-q27', 'sat-cs-tone-attitude', 'Write a sentence that shows agreement with someone else''s idea.', 2, 'I fully agree with the proposal to increase funding for education.', '{"answer": "A sentence expressing alignment with another viewpoint", "explanation": "Should use agreement words like agree, support, or concur"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 27),
('sat-cs-q28', 'sat-cs-tone-attitude', 'Write a sentence that shows disagreement with someone else''s idea.', 2, 'I must respectfully disagree with the assertion that technology harms social skills.', '{"answer": "A sentence expressing opposition to another viewpoint", "explanation": "Should politely express disagreement using words like disagree, contest, or challenge"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 28),
('sat-cs-q29', 'sat-cs-tone-attitude', 'Write a sentence that could be used as a final concluding statement.', 2, 'In conclusion, these findings demonstrate the urgent need for policy reform.', '{"answer": "A sentence wrapping up main points", "explanation": "Should use concluding phrases and summarize key takeaways"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 29),
('sat-cs-q30', 'sat-cs-tone-attitude', 'Write a sentence that introduces a new idea or topic smoothly.', 2, 'Turning now to the economic implications, we must consider the broader market trends.', '{"answer": "A sentence transitioning to a new subject", "explanation": "Should use transitional phrases that connect ideas smoothly"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 30);