INSERT INTO curriculum_questions (id, topic_id, question, marks, model_answer, marking_criteria, difficulty, spec_reference, calculator_guidance, order_index) VALUES
('sat-ei-q1', 'sat-ei-revision', 'Write a short sentence supporting the idea of healthier school lunches.', 2, 'Nutritious school meals help students focus better and perform well academically.', '{"answer": "A sentence advocating for healthy food in schools", "explanation": "Should emphasize benefits like health, concentration, or academic performance"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 1),
('sat-ei-q2', 'sat-ei-revision', 'Write a short sentence supporting the idea of public transportation improvements.', 2, 'Expanding public transit reduces traffic congestion and lowers carbon emissions.', '{"answer": "A sentence supporting better public transport", "explanation": "Should mention environmental, economic, or accessibility benefits"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 2),
('sat-ei-q3', 'sat-ei-revision', 'Write a short sentence supporting the idea of longer school breaks.', 2, 'Extended breaks allow students time to recharge and return to school more focused.', '{"answer": "A sentence advocating for more vacation time", "explanation": "Should focus on rest, mental health, or improved performance"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 3),
('sat-ei-q4', 'sat-ei-revision', 'Write a short sentence supporting the idea of community volunteering.', 2, 'Volunteering strengthens community bonds and helps individuals develop empathy.', '{"answer": "A sentence supporting volunteer work", "explanation": "Should emphasize community, personal growth, or social responsibility"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 4),
('sat-ei-q5', 'sat-ei-revision', 'Write a short sentence supporting the idea of school clubs and activities.', 2, 'Extracurricular activities foster teamwork and provide valuable leadership experience.', '{"answer": "A sentence promoting school clubs", "explanation": "Should mention social skills, interests, or personal development"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 5),
('sat-ei-q6', 'sat-ei-revision', 'Write a short sentence supporting the idea of reducing screen time.', 2, 'Limiting screen time improves sleep quality and encourages physical activity.', '{"answer": "A sentence advocating for less device use", "explanation": "Should focus on health, wellbeing, or productivity benefits"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 6),
('sat-ei-q7', 'sat-ei-revision', 'Write a sentence that challenges or argues against one of the previous ideas.', 3, 'However, some argue that extended school breaks may cause students to forget important material.', '{"answer": "A sentence presenting a counterpoint", "explanation": "Should respectfully present an opposing perspective"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 7),
('sat-ei-q8', 'sat-ei-revision', 'Write a sentence that introduces a new idea using a transition word (finally, next, also).', 2, 'Additionally, schools should consider implementing mindfulness programs to reduce student stress.', '{"answer": "A sentence with a transitional word", "explanation": "Should smoothly connect to previous ideas using transitions"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 8),
('sat-ei-q9', 'sat-ei-revision', 'Write a sentence showing a logical reason for an opinion.', 2, 'Students benefit from arts education because it enhances creativity and critical thinking.', '{"answer": "A sentence with clear reasoning", "explanation": "Should use because, since, or as to show causation"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 9),
('sat-ei-q10', 'sat-ei-revision', 'Write a sentence giving a specific example to support a claim.', 2, 'For instance, students who participate in music programs often score higher on standardized tests.', '{"answer": "A sentence providing concrete evidence", "explanation": "Should include a specific case or example"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 10),
('sat-ei-q11', 'sat-ei-revision', 'Write a sentence using a number or fact to strengthen an argument.', 3, 'Research shows that 75 percent of employers value teamwork skills gained through group activities.', '{"answer": "A sentence with statistics or data", "explanation": "Should include quantitative evidence"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 11),
('sat-ei-q12', 'sat-ei-revision', 'Write a sentence asking a rhetorical question to make a point.', 2, 'How can we expect students to thrive if we do not invest in their education?', '{"answer": "A question that emphasizes a point", "explanation": "Should be persuasive and not require an actual answer"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 12),
('sat-ei-q13', 'sat-ei-revision', 'Write a sentence that states a clear claim.', 2, 'Every student deserves access to quality education regardless of their background.', '{"answer": "A direct statement of position", "explanation": "Should be assertive and unambiguous"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 13),
('sat-ei-q14', 'sat-ei-revision', 'Write a sentence that provides a supporting detail.', 2, 'Studies indicate that smaller class sizes lead to more personalized instruction.', '{"answer": "A sentence offering evidence", "explanation": "Should back up a main idea with specifics"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 14),
('sat-ei-q15', 'sat-ei-revision', 'Write a sentence that provides background information before an argument.', 2, 'Over the past decade, educational funding has decreased significantly in many districts.', '{"answer": "A sentence setting context", "explanation": "Should establish necessary information before making a point"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 15),
('sat-ei-q16', 'sat-ei-revision', 'Write a sentence that explains a benefit of something.', 2, 'Online learning platforms offer students flexibility to study at their own pace.', '{"answer": "A sentence describing an advantage", "explanation": "Should clearly state a positive outcome"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 16),
('sat-ei-q17', 'sat-ei-revision', 'Write a sentence that explains a possible downside of something.', 2, 'However, excessive reliance on technology may reduce face-to-face social interactions.', '{"answer": "A sentence identifying a drawback", "explanation": "Should present a potential negative consequence"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 17),
('sat-ei-q18', 'sat-ei-revision', 'Write a sentence that makes a recommendation.', 2, 'Schools should incorporate more hands-on learning experiences into their curricula.', '{"answer": "A sentence suggesting action", "explanation": "Should use should, must, or ought to"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 18),
('sat-ei-q19', 'sat-ei-revision', 'Write a sentence that expresses a prediction.', 2, 'If current trends continue, digital literacy will become essential for all careers.', '{"answer": "A sentence forecasting future outcomes", "explanation": "Should use will, likely, or conditional language"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 19),
('sat-ei-q20', 'sat-ei-revision', 'Write a sentence that gives a warning.', 2, 'Neglecting student mental health could result in serious long-term consequences.', '{"answer": "A sentence cautioning about risks", "explanation": "Should convey urgency about potential problems"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 20),
('sat-ei-q21', 'sat-ei-revision', 'Write a sentence beginning with a transition showing contrast (however, although).', 2, 'Although technology offers many advantages, it cannot replace human interaction entirely.', '{"answer": "A contrasting sentence", "explanation": "Should present an opposing or limiting idea"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 21),
('sat-ei-q22', 'sat-ei-revision', 'Write a sentence beginning with a transition showing addition (also, in addition).', 2, 'Furthermore, collaborative projects help students develop essential communication skills.', '{"answer": "A sentence adding information", "explanation": "Should build upon previous points"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 22),
('sat-ei-q23', 'sat-ei-revision', 'Write a sentence beginning with a transition showing cause and effect (therefore, so).', 2, 'Consequently, schools must allocate resources to support diverse learning needs.', '{"answer": "A sentence showing logical result", "explanation": "Should connect cause to outcome"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 23),
('sat-ei-q24', 'sat-ei-revision', 'Write a sentence beginning with a transition showing conclusion (in conclusion, overall).', 2, 'In conclusion, investing in education benefits both individuals and society as a whole.', '{"answer": "A concluding sentence", "explanation": "Should summarize key points"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 24),
('sat-ei-q25', 'sat-ei-revision', 'Write a sentence giving an example beginning with "For example,".', 2, 'For example, students who read regularly demonstrate stronger vocabulary and comprehension skills.', '{"answer": "A sentence providing illustration", "explanation": "Should offer a specific instance"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 25),
('sat-ei-q26', 'sat-ei-revision', 'Write a sentence that mentions both sides of an argument.', 3, 'While standardized tests provide measurable data, they may not capture every aspect of student ability.', '{"answer": "A balanced sentence", "explanation": "Should acknowledge both perspectives"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 26),
('sat-ei-q27', 'sat-ei-revision', 'Write a short sentence that stays on one topic only.', 2, 'Effective time management helps students balance academic and personal commitments.', '{"answer": "A focused sentence", "explanation": "Should address only one clear idea"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 27),
('sat-ei-q28', 'sat-ei-revision', 'Write a sentence explaining why focus is important in writing.', 2, 'Maintaining focus ensures that writing is clear, coherent, and persuasive.', '{"answer": "A sentence about writing clarity", "explanation": "Should explain benefits of staying on topic"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 28),
('sat-ei-q29', 'sat-ei-revision', 'Write a sentence that could be used as the first sentence of a paragraph.', 2, 'Modern education faces numerous challenges that require innovative solutions.', '{"answer": "An opening/topic sentence", "explanation": "Should introduce the main idea clearly"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 29),
('sat-ei-q30', 'sat-ei-revision', 'Write a sentence that could be used as the last sentence of a paragraph.', 2, 'Thus, addressing these issues remains a critical priority for educators and policymakers alike.', '{"answer": "A closing sentence", "explanation": "Should wrap up the paragraph''s main point"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 30);