INSERT INTO curriculum_questions (id, topic_id, question, marks, model_answer, marking_criteria, difficulty, spec_reference, calculator_guidance, order_index) VALUES
('sat-sec-q1', 'sat-sec-grammar', 'Write a sentence using correct grammar with the phrase: "Neither the students nor the teacher..."', 3, 'Neither the students nor the teacher was aware of the schedule change.', '{"answer": "A grammatically correct sentence with neither...nor", "explanation": "Verb should agree with the subject nearest to it (teacher = singular)"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 1),
('sat-sec-q2', 'sat-sec-grammar', 'Write a sentence with three items in a list using commas correctly.', 2, 'The assignment requires research, analysis, and a written report.', '{"answer": "A sentence with proper comma usage in a series", "explanation": "Should use commas to separate three or more items"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 2),
('sat-sec-q3', 'sat-sec-grammar', 'Write a sentence using "each" and make sure the pronoun later in the sentence matches (singular).', 3, 'Each student must submit his or her project by Friday.', '{"answer": "A sentence with pronoun agreement", "explanation": "Each is singular, so pronoun should be singular (his/her, their is also acceptable)"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 3),
('sat-sec-q4', 'sat-sec-modifiers', 'Write a sentence where the describing phrase (modifier) is in the correct place.', 3, 'Walking through the park, I noticed the beautiful flowers.', '{"answer": "A sentence with correctly placed modifier", "explanation": "The modifier should clearly refer to the subject doing the action"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 4),
('sat-sec-q5', 'sat-sec-parallel', 'Write a sentence where all items in a list use the same verb form (parallel structure).', 3, 'She enjoys reading, writing, and painting in her free time.', '{"answer": "A sentence with parallel structure", "explanation": "All verbs should be in the same form (gerunds: -ing)"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 5),
('sat-sec-q6', 'sat-sec-grammar', 'Write a sentence that stays in one verb tense (either all past or all present).', 2, 'The scientist conducts experiments and analyzes the results carefully.', '{"answer": "A sentence with consistent verb tense", "explanation": "Should not switch between past and present tense"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 6),
('sat-sec-q7', 'sat-sec-grammar', 'Write a sentence using the word "data" with the correct verb (plural).', 2, 'The data show a clear trend over the past decade.', '{"answer": "A sentence treating data as plural", "explanation": "Data is traditionally plural (datum = singular)"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 7),
('sat-sec-q8', 'sat-sec-punctuation', 'Write a sentence that begins with a short phrase and uses a comma after it.', 2, 'After finishing the exam, students submitted their papers.', '{"answer": "A sentence with an introductory phrase and comma", "explanation": "Should use comma after introductory element"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 8),
('sat-sec-q9', 'sat-sec-pronoun', 'Write a sentence where a group noun (like team, class, committee) uses the correct pronoun.', 3, 'The committee reached its decision after thorough deliberation.', '{"answer": "A sentence with collective noun pronoun agreement", "explanation": "Collective nouns typically take singular pronouns (its)"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 9),
('sat-sec-q10', 'sat-sec-modifiers', 'Write a sentence where the word "only" or "almost" is placed correctly so the meaning is clear.', 3, 'She only eats vegetables for dinner, not meat.', '{"answer": "A sentence with correctly placed limiting modifier", "explanation": "Position of only/almost changes meaning"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 10),
('sat-sec-q11', 'sat-sec-parallel', 'Write a sentence using a list of verbs written in the same form.', 2, 'The coach advised us to practice daily, to eat well, and to rest adequately.', '{"answer": "A sentence with parallel verb forms", "explanation": "All verbs should use the same structure (infinitives)"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 11),
('sat-sec-q12', 'sat-sec-verb-tense', 'Write one sentence using the past perfect tense (for example: "had eaten").', 3, 'By the time we arrived, the meeting had already started.', '{"answer": "A sentence with past perfect tense", "explanation": "Use had + past participle for action before another past action"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 12),
('sat-sec-q13', 'sat-sec-grammar', 'Write a sentence with a subject joined by or, and make sure the verb agrees.', 3, 'Either the teacher or the students are responsible for organizing the event.', '{"answer": "A sentence with correct verb agreement", "explanation": "Verb agrees with the nearer subject (students = plural)"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 13),
('sat-sec-q14', 'sat-sec-punctuation', 'Write a sentence that includes a phrase in the middle that needs commas around it.', 3, 'My brother, who lives in London, visits us every summer.', '{"answer": "A sentence with a non-restrictive clause", "explanation": "Use commas to set off non-essential information"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 14),
('sat-sec-q15', 'sat-sec-pronoun', 'Write a sentence comparing two things using the correct pronoun (for example: than me / than I).', 3, 'She is taller than I am.', '{"answer": "A sentence with correct pronoun case in comparison", "explanation": "Use subject pronoun if verb follows (I am), object pronoun if not"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 15),
('sat-sec-q16', 'sat-sec-modifiers', 'Start a sentence with a phrase like "After studying…" and make sure it doesn''t create a dangling modifier.', 3, 'After studying for hours, I finally understood the concept.', '{"answer": "A sentence with correctly attached modifier", "explanation": "Subject I immediately follows the modifying phrase"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 16),
('sat-sec-q17', 'sat-sec-parallel', 'Write a sentence using both infinitives and gerunds but keep the structure parallel.', 3, 'To succeed requires planning, dedication, and working hard.', '{"answer": "A sentence with consistent verb forms", "explanation": "Should use all infinitives or all gerunds in a series"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 17),
('sat-sec-q18', 'sat-sec-punctuation', 'Write a sentence using a semicolon correctly between two complete ideas.', 3, 'The experiment succeeded; the results exceeded all expectations.', '{"answer": "A sentence with correct semicolon usage", "explanation": "Semicolon joins two independent clauses"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 18),
('sat-sec-q19', 'sat-sec-pronoun', 'Write a sentence using a reflexive pronoun (myself, himself, herself, themselves) correctly.', 2, 'She completed the entire project herself without any assistance.', '{"answer": "A sentence with correct reflexive pronoun", "explanation": "Reflexive pronouns emphasize or refer back to the subject"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 19),
('sat-sec-q20', 'sat-sec-verb-tense', 'Write a sentence describing progress while keeping the verb form consistent.', 2, 'The team has been working diligently and is making steady progress.', '{"answer": "A sentence with consistent progressive tense", "explanation": "Should maintain same tense throughout"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 20),
('sat-sec-q21', 'sat-sec-grammar', 'Write a sentence using the group noun "team" with the correct verb (is or are, depending on meaning).', 3, 'The team is practicing for the championship game.', '{"answer": "A sentence with collective noun agreement", "explanation": "Team as a unit takes singular verb (is)"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 21),
('sat-sec-q22', 'sat-sec-possessives', 'Write a sentence with a plural possessive noun (example: students'' backpacks).', 2, 'The teachers'' lounge is located on the second floor.', '{"answer": "A sentence with correct plural possessive", "explanation": "Apostrophe goes after the s for plural possessives"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 22),
('sat-sec-q23', 'sat-sec-pronoun', 'Write a sentence where the pronoun stays singular or plural the entire time—no switching.', 2, 'When a student works hard, he or she usually sees improvement.', '{"answer": "A sentence with consistent pronoun number", "explanation": "Should not switch from singular to plural or vice versa"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 23),
('sat-sec-q24', 'sat-sec-modifiers', 'Write a sentence with a phrase in the correct place so the meaning is not confusing.', 3, 'I nearly finished all my homework before dinner.', '{"answer": "A sentence with clear modification", "explanation": "Modifier placement affects meaning clarity"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 24),
('sat-sec-q25', 'sat-sec-grammar', 'Write a sentence comparing two things using as…as or more than / less than correctly.', 2, 'This book is as interesting as the one I read last month.', '{"answer": "A sentence with correct comparison structure", "explanation": "Use as...as for equal comparison, more/less for inequality"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 25),
('sat-sec-q26', 'sat-sec-verb-tense', 'Write a sentence giving advice or a recommendation using the correct verb form (example: "I suggest that he study.").', 3, 'The committee recommends that every student participate in the program.', '{"answer": "A sentence with subjunctive mood", "explanation": "Use base form of verb after suggest, recommend, require"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 26),
('sat-sec-q27', 'sat-sec-punctuation', 'Write a sentence that joins two complete ideas without a comma splice.', 3, 'The results were promising, so we decided to continue the study.', '{"answer": "A sentence correctly joining independent clauses", "explanation": "Use comma + coordinating conjunction or semicolon"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 27),
('sat-sec-q28', 'sat-sec-punctuation', 'Write a sentence with a hyphenated adjective before a noun (example: full-time job).', 2, 'She secured a well-paying position at a reputable company.', '{"answer": "A sentence with correctly hyphenated compound modifier", "explanation": "Hyphenate compound adjectives before nouns"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 28),
('sat-sec-q29', 'sat-sec-parallel', 'Write a sentence showing parallel structure where a repeated word is left out because it''s understood.', 3, 'The students read the assignment, analyzed the data, and wrote their conclusions.', '{"answer": "A sentence with parallel verb structure", "explanation": "All verbs should be in same tense and form"}', 'medium', 'SAT Reading & Writing', 'non-calc-friendly', 29),
('sat-sec-q30', 'sat-sec-grammar', 'Write a sentence using the correct form of an adjective (good → better → best).', 2, 'This solution is better than the previous one we tried.', '{"answer": "A sentence with correct comparative form", "explanation": "Use comparative form when comparing two things"}', 'easy', 'SAT Reading & Writing', 'non-calc-friendly', 30);