INSERT INTO curriculum_questions (id, topic_id, question, difficulty, model_answer, marking_criteria, marks, spec_reference, order_index, calculator_guidance) VALUES
('sat-info-001', 'sat-information-ideas-general', 'Write the main idea of a short paragraph you create about any topic.', 'easy', 'Example: "Social media has changed how people communicate by making it faster but less personal." The main idea clearly states the topic and the key point about it in one sentence.', '["Includes a clear topic", "States the central point or message", "Is concise and complete", "Uses appropriate academic language"]', 1, 'SAT Information & Ideas', 1, 'calc-recommended'),
('sat-info-002', 'sat-information-ideas-general', 'Write one sentence that clearly states a claim about something (example: school, technology, health).', 'easy', 'Example: "Schools should start later in the morning to improve student performance." This is a clear, debatable claim that takes a position.', '["Makes a clear, specific statement", "Is debatable (not a fact)", "Takes a position on the topic", "Is written in complete sentence"]', 1, 'SAT Information & Ideas', 2, 'calc-recommended'),
('sat-info-003', 'sat-information-ideas-general', 'Write one sentence that gives a reason supporting your claim.', 'easy', 'Example: "Later start times would allow students to get adequate sleep, which improves focus and memory." This directly supports the claim with logical reasoning.', '["Directly supports the claim", "Provides logical reasoning", "Is relevant to the topic", "Uses clear cause-effect relationship"]', 1, 'SAT Information & Ideas', 3, 'calc-recommended'),
('sat-info-004', 'sat-information-ideas-general', 'Write a sentence that includes a fact that could support an argument.', 'easy', 'Example: "According to the CDC, teenagers need 8-10 hours of sleep per night for optimal health." This is a verifiable fact that can support an argument.', '["States a verifiable fact", "Includes a credible source if applicable", "Is relevant to an argument", "Is specific and accurate"]', 1, 'SAT Information & Ideas', 4, 'calc-recommended'),
('sat-info-005', 'sat-information-ideas-general', 'Write a sentence that introduces a clear example for a claim.', 'medium', 'Example: "For instance, students at schools with 8:30 AM start times report feeling more alert during first period." This provides a specific example supporting a claim.', '["Uses transitional phrase (for example, for instance)", "Provides specific, concrete example", "Clearly relates to the claim", "Is detailed enough to be meaningful"]', 1, 'SAT Information & Ideas', 5, 'calc-recommended'),
('sat-info-006', 'sat-information-ideas-general', 'Write a sentence that includes a statistic or number as evidence.', 'medium', 'Example: "Research shows that 73% of high school students are sleep-deprived during the school week." This uses numerical data as evidence.', '["Includes specific number or percentage", "Attributes data to a source", "Is relevant to the argument", "Presents data accurately"]', 1, 'SAT Information & Ideas', 6, 'calc-recommended'),
('sat-info-007', 'sat-information-ideas-general', 'Write a sentence that explains why your evidence supports your claim.', 'medium', 'Example: "This statistic demonstrates that current school schedules conflict with teenage sleep needs, supporting the case for later start times." This connects evidence to claim.', '["Explicitly connects evidence to claim", "Uses logical reasoning", "Explains the significance", "Uses appropriate transition words"]', 1, 'SAT Information & Ideas', 7, 'calc-recommended'),
('sat-info-008', 'sat-information-ideas-general', 'Write a sentence that identifies the purpose of a text (example: to explain, persuade, entertain).', 'easy', 'Example: "The purpose of this article is to persuade readers that renewable energy is essential for environmental sustainability." This clearly identifies the author''s intent.', '["Clearly states the purpose", "Uses appropriate purpose verb (explain, persuade, inform, entertain)", "Relates to the text content", "Is written clearly"]', 1, 'SAT Information & Ideas', 8, 'calc-recommended'),
('sat-info-009', 'sat-information-ideas-general', 'Write a sentence that would appear in a text meant to inform the reader.', 'easy', 'Example: "Photosynthesis is the process by which plants convert sunlight into chemical energy." This presents factual information objectively.', '["Presents factual information", "Uses objective, neutral language", "Is clear and educational", "Avoids opinion or persuasion"]', 1, 'SAT Information & Ideas', 9, 'calc-recommended'),
('sat-info-010', 'sat-information-ideas-general', 'Write a sentence that would appear in a text meant to persuade the reader.', 'easy', 'Example: "We must take action now to protect our oceans before it is too late." This uses persuasive language to convince readers.', '["Uses persuasive language", "Takes a clear position", "Includes call to action or strong opinion", "Appeals to reader emotions or logic"]', 1, 'SAT Information & Ideas', 10, 'calc-recommended'),
('sat-info-011', 'sat-information-ideas-general', 'Write a sentence that would appear in a text meant to describe something.', 'easy', 'Example: "The ancient cathedral featured towering spires, intricate stained glass windows, and weathered stone walls covered in moss." This provides vivid descriptive details.', '["Uses descriptive, sensory language", "Paints a clear picture", "Includes specific details", "Uses appropriate adjectives"]', 1, 'SAT Information & Ideas', 11, 'calc-recommended'),
('sat-info-012', 'sat-information-ideas-general', 'Write a sentence giving one reliable source for research.', 'medium', 'Example: "Peer-reviewed academic journals provide reliable, expert-verified information for research." This identifies a credible source type.', '["Names a credible source type", "Explains why it is reliable", "Is appropriate for academic research", "Is specific enough to be useful"]', 1, 'SAT Information & Ideas', 12, 'calc-recommended'),
('sat-info-013', 'sat-information-ideas-general', 'Write a sentence giving one unreliable source for research.', 'medium', 'Example: "Personal blogs without cited sources are generally unreliable for academic research." This identifies and explains an unreliable source.', '["Names an unreliable source type", "Explains why it is unreliable", "Shows understanding of source credibility", "Is specific"]', 1, 'SAT Information & Ideas', 13, 'calc-recommended'),
('sat-info-014', 'sat-information-ideas-general', 'Write a sentence that summarizes a topic using only the most important point.', 'medium', 'Example: "Climate change is primarily caused by human activities that increase greenhouse gas emissions." This distills complex information to key point.', '["Identifies the most important point", "Is concise and focused", "Captures the essence of the topic", "Avoids unnecessary details"]', 1, 'SAT Information & Ideas', 14, 'calc-recommended'),
('sat-info-015', 'sat-information-ideas-general', 'Write a sentence that makes a prediction based on evidence.', 'hard', 'Example: "Given current deforestation rates, the Amazon rainforest could lose 27% of its trees by 2030." This makes a logical prediction from data.', '["Makes a specific prediction", "Based on stated evidence", "Uses logical reasoning", "Is realistic and credible"]', 1, 'SAT Information & Ideas', 15, 'calc-recommended'),
('sat-info-016', 'sat-information-ideas-general', 'Write a sentence that shows a cause and effect relationship.', 'medium', 'Example: "Because of increased air pollution, respiratory illnesses have risen by 15% in urban areas." This clearly shows cause leading to effect.', '["Identifies a clear cause", "Shows the resulting effect", "Uses appropriate connecting words (because, therefore, consequently)", "Shows logical relationship"]', 1, 'SAT Information & Ideas', 16, 'calc-recommended'),
('sat-info-017', 'sat-information-ideas-general', 'Write a sentence comparing two things to show how they are similar.', 'medium', 'Example: "Both wind and solar energy provide clean, renewable power without producing greenhouse gases." This highlights shared characteristics.', '["Identifies two items to compare", "Shows specific similarities", "Uses comparison language (both, similarly, like)", "Is clear and accurate"]', 1, 'SAT Information & Ideas', 17, 'calc-recommended'),
('sat-info-018', 'sat-information-ideas-general', 'Write a sentence contrasting two things to show how they are different.', 'medium', 'Example: "Unlike fossil fuels, which release harmful emissions, renewable energy sources produce clean power." This highlights key differences.', '["Identifies two items to contrast", "Shows specific differences", "Uses contrast language (unlike, however, whereas)", "Is clear and accurate"]', 1, 'SAT Information & Ideas', 18, 'calc-recommended'),
('sat-info-019', 'sat-information-ideas-general', 'Write a question that someone could research using data.', 'medium', 'Example: "How does daily exercise duration correlate with cardiovascular health in adults over 50?" This is specific and researchable.', '["Is specific and focused", "Can be answered with data", "Is clear and well-defined", "Is feasible to research"]', 1, 'SAT Information & Ideas', 19, 'calc-recommended'),
('sat-info-020', 'sat-information-ideas-general', 'Write a sentence that shows an inference (something not directly stated but understood).', 'hard', 'Example: "The empty shelves and long lines suggest that shoppers are preparing for the approaching storm." This draws conclusion from observations.', '["Makes logical inference from evidence", "Is not directly stated but reasonably concluded", "Uses appropriate language (suggest, imply, indicate)", "Shows critical thinking"]', 1, 'SAT Information & Ideas', 20, 'calc-recommended'),
('sat-info-021', 'sat-information-ideas-general', 'Write a sentence that includes a detail that is relevant to the topic.', 'easy', 'Example: "The new library will include study rooms, computer stations, and a collection of over 50,000 books." These details directly relate to the library topic.', '["Provides specific detail", "Directly relates to main topic", "Adds meaningful information", "Is appropriate and accurate"]', 1, 'SAT Information & Ideas', 21, 'calc-recommended'),
('sat-info-022', 'sat-information-ideas-general', 'Write a sentence that includes a detail that is irrelevant to the topic.', 'medium', 'Example: "The new library will open next month; the architect enjoys hiking on weekends." The second detail is unrelated to the library.', '["Includes detail that does not relate to main topic", "Shows understanding of relevance", "Demonstrates what should be omitted", "Makes the irrelevance clear"]', 1, 'SAT Information & Ideas', 22, 'calc-recommended'),
('sat-info-023', 'sat-information-ideas-general', 'Write a sentence that would support an argument for public libraries.', 'medium', 'Example: "Public libraries provide free access to information, technology, and educational resources for all community members." This supports libraries'' value.', '["Presents benefit or value of libraries", "Uses persuasive reasoning", "Is specific and clear", "Supports pro-library position"]', 1, 'SAT Information & Ideas', 23, 'calc-recommended'),
('sat-info-024', 'sat-information-ideas-general', 'Write a sentence that would support an argument for renewable energy.', 'medium', 'Example: "Renewable energy reduces dependence on fossil fuels while creating new jobs in growing industries." This presents multiple benefits.', '["Presents benefit of renewable energy", "Uses persuasive reasoning", "Is specific and clear", "Supports renewable energy adoption"]', 1, 'SAT Information & Ideas', 24, 'calc-recommended'),
('sat-info-025', 'sat-information-ideas-general', 'Write a sentence that would support an argument for exercise or fitness.', 'medium', 'Example: "Regular exercise reduces the risk of chronic diseases, improves mental health, and increases overall life expectancy." This highlights key benefits.', '["Presents benefits of exercise", "Uses persuasive reasoning", "Is specific and clear", "Supports exercise/fitness"]', 1, 'SAT Information & Ideas', 25, 'calc-recommended'),
('sat-info-026', 'sat-information-ideas-general', 'Write a sentence that explains what type of evidence would best support a claim (example: statistics, examples, expert opinions).', 'hard', 'Example: "To support the claim that meditation reduces stress, peer-reviewed studies with measurable health outcomes would provide the strongest evidence." This identifies appropriate evidence type.', '["Identifies specific evidence type", "Explains why it is effective", "Relates to the claim", "Shows understanding of evidence quality"]', 1, 'SAT Information & Ideas', 26, 'calc-recommended'),
('sat-info-027', 'sat-information-ideas-general', 'Write a sentence that correctly uses a counterclaim (an opposite view).', 'hard', 'Example: "While some argue that homework is unnecessary, research shows it reinforces learning and develops study skills." This acknowledges opposing view.', '["Presents opposing viewpoint", "Uses appropriate transition (while, although)", "Is stated fairly and accurately", "Sets up for rebuttal"]', 1, 'SAT Information & Ideas', 27, 'calc-recommended'),
('sat-info-028', 'sat-information-ideas-general', 'Write a sentence that responds to a counterclaim with reasoning.', 'hard', 'Example: "However, when homework is purposeful and moderate, it enhances understanding without causing excessive stress." This refutes counterclaim with reasoning.', '["Directly addresses counterclaim", "Provides logical reasoning", "Uses appropriate transition (however, yet)", "Strengthens original argument"]', 1, 'SAT Information & Ideas', 28, 'calc-recommended'),
('sat-info-029', 'sat-information-ideas-general', 'Write a one-sentence conclusion that clearly restates a claim.', 'medium', 'Example: "In conclusion, investing in renewable energy is essential for environmental sustainability and economic growth." This effectively restates main argument.', '["Uses conclusion transition phrase", "Restates main claim clearly", "Is concise and powerful", "Provides sense of closure"]', 1, 'SAT Information & Ideas', 29, 'calc-recommended'),
('sat-info-030', 'sat-information-ideas-general', 'Write a sentence that combines evidence and reasoning into one strong statement.', 'hard', 'Example: "Since 90% of ocean pollution comes from land-based sources, reducing plastic use and improving waste management are critical steps toward protecting marine ecosystems." This integrates evidence and reasoning.', '["Includes specific evidence", "Provides clear reasoning", "Connects evidence to conclusion", "Is cohesive and powerful"]', 1, 'SAT Information & Ideas', 30, 'calc-recommended');