INSERT INTO curriculum_questions (id, topic_id, question, difficulty, model_answer, marking_criteria, marks, spec_reference, order_index, calculator_guidance) VALUES
('sat-craft-001', 'sat-craft-structure-general', 'Write a sentence that shows an excited tone about a new idea.', 'easy', 'Example: "This revolutionary approach could completely transform how we solve environmental challenges!" The exclamation and enthusiastic word choice convey excitement.', '["Uses enthusiastic language", "Includes appropriate punctuation (exclamation)", "Shows positive emotion clearly", "Is appropriate for context"]', 1, 'SAT Craft & Structure', 1, 'calc-recommended'),
('sat-craft-002', 'sat-craft-structure-general', 'Write a sentence that shows a disappointed tone about a result.', 'easy', 'Example: "Unfortunately, the experiment failed to produce the expected results despite careful preparation." The word choice and context convey disappointment.', '["Uses words conveying disappointment", "Maintains appropriate register", "Shows emotion clearly", "Is grammatically correct"]', 1, 'SAT Craft & Structure', 2, 'calc-recommended'),
('sat-craft-003', 'sat-craft-structure-general', 'Write a sentence that uses a neutral and factual tone.', 'easy', 'Example: "The study included 500 participants who were monitored over a six-month period." This presents information objectively without emotion.', '["Uses objective language", "Avoids emotional words", "Presents facts clearly", "Maintains professional tone"]', 1, 'SAT Craft & Structure', 3, 'calc-recommended'),
('sat-craft-004', 'sat-craft-structure-general', 'Write a sentence comparing two things using a compare-and-contrast structure.', 'medium', 'Example: "While electric cars produce zero emissions, traditional gasoline vehicles remain more affordable for most consumers." This shows both similarity and difference.', '["Identifies two items", "Shows comparison/contrast", "Uses appropriate transition (while, whereas)", "Is balanced and clear"]', 1, 'SAT Craft & Structure', 4, 'calc-recommended'),
('sat-craft-005', 'sat-craft-structure-general', 'Write a sentence explaining why something happened using cause and effect.', 'medium', 'Example: "The bridge collapsed because repeated stress from heavy traffic weakened its structural supports over time." This clearly shows cause leading to effect.', '["Identifies clear cause", "Shows resulting effect", "Uses causal connector (because, since, due to)", "Shows logical relationship"]', 1, 'SAT Craft & Structure', 5, 'calc-recommended'),
('sat-craft-006', 'sat-craft-structure-general', 'Write a sentence telling steps in order using a sequence structure.', 'easy', 'Example: "First, gather all materials; then, measure the ingredients carefully; finally, mix them together until smooth." This shows clear sequential steps.', '["Uses sequence words (first, then, finally)", "Shows clear order", "Is logical and organized", "Is easy to follow"]', 1, 'SAT Craft & Structure', 6, 'calc-recommended'),
('sat-craft-007', 'sat-craft-structure-general', 'Write a sentence that shows the writer is uncertain about an outcome.', 'medium', 'Example: "It remains unclear whether the new policy will effectively address the underlying issues." Language indicates uncertainty.', '["Uses uncertain language (unclear, perhaps, might)", "Avoids definitive statements", "Maintains appropriate tone", "Shows appropriate caution"]', 1, 'SAT Craft & Structure', 7, 'calc-recommended'),
('sat-craft-008', 'sat-craft-structure-general', 'Write a sentence that shows the writer is confident about a solution.', 'medium', 'Example: "This approach will undoubtedly solve the problem and deliver measurable results." Language conveys strong confidence.', '["Uses confident language (will, certainly, undoubtedly)", "Makes definitive statement", "Shows conviction", "Maintains appropriate assertiveness"]', 1, 'SAT Craft & Structure', 8, 'calc-recommended'),
('sat-craft-009', 'sat-craft-structure-general', 'Write a sentence that presents a problem clearly.', 'easy', 'Example: "Traffic congestion in the city center has increased commute times by 40%, frustrating residents and businesses alike." This clearly states the problem.', '["Identifies specific problem", "Provides clear description", "Shows impact or significance", "Is concise and direct"]', 1, 'SAT Craft & Structure', 9, 'calc-recommended'),
('sat-craft-010', 'sat-craft-structure-general', 'Write a sentence that presents a solution to a problem you mentioned.', 'medium', 'Example: "Implementing dedicated bus lanes and expanding public transit would reduce traffic and improve commute times." This offers a specific solution.', '["Proposes specific solution", "Relates directly to problem", "Is practical and feasible", "Shows logical thinking"]', 1, 'SAT Craft & Structure', 10, 'calc-recommended'),
('sat-craft-011', 'sat-craft-structure-general', 'Write a sentence that includes evidence or a fact to support a claim.', 'medium', 'Example: "According to the Environmental Protection Agency, air quality has improved by 25% since the new regulations were implemented." This provides factual support.', '["Includes specific evidence", "Cites source when appropriate", "Supports a claim", "Is relevant and accurate"]', 1, 'SAT Craft & Structure', 11, 'calc-recommended'),
('sat-craft-012', 'sat-craft-structure-general', 'Write a sentence that shows a contrast using a connecting word like "however" or "but".', 'medium', 'Example: "The plan appeared promising on paper; however, implementation revealed several unforeseen challenges." This effectively shows contrast.', '["Uses contrast connector (however, but, yet)", "Shows clear contrast", "Has two distinct ideas", "Is grammatically correct"]', 1, 'SAT Craft & Structure', 12, 'calc-recommended'),
('sat-craft-013', 'sat-craft-structure-general', 'Write a sentence that shows the writer is skeptical about an idea.', 'medium', 'Example: "The proposed solution seems overly simplistic and unlikely to address the complexity of the issue." Language indicates skepticism.', '["Shows doubt or questioning", "Uses skeptical language (seems, unlikely, questionable)", "Maintains respectful tone", "Provides reasoning for skepticism"]', 1, 'SAT Craft & Structure', 13, 'calc-recommended'),
('sat-craft-014', 'sat-craft-structure-general', 'Write a sentence that shows the writer is supportive of a proposal.', 'medium', 'Example: "This initiative represents a thoughtful, well-researched approach that deserves strong support from the community." Language shows clear support.', '["Shows clear support", "Uses positive language", "Maintains appropriate tone", "Provides reasoning for support"]', 1, 'SAT Craft & Structure', 14, 'calc-recommended'),
('sat-craft-015', 'sat-craft-structure-general', 'Write a sentence that shows the writer is warning about a risk or danger.', 'medium', 'Example: "Without immediate action, we risk losing irreplaceable ecosystems and the biodiversity they support." This conveys warning tone.', '["Identifies specific risk", "Uses warning language (risk, danger, threaten)", "Shows urgency", "Is clear about consequences"]', 1, 'SAT Craft & Structure', 15, 'calc-recommended'),
('sat-craft-016', 'sat-craft-structure-general', 'Write a sentence that clearly summarizes an idea in one line.', 'easy', 'Example: "In essence, the study confirms that regular exercise significantly improves both physical and mental health." This concisely summarizes key point.', '["Is concise and focused", "Captures main idea", "Uses summary transition (in essence, in summary)", "Is complete and clear"]', 1, 'SAT Craft & Structure', 16, 'calc-recommended'),
('sat-craft-017', 'sat-craft-structure-general', 'Write a sentence that shows the writer is surprised by a result.', 'medium', 'Example: "Surprisingly, the less expensive option actually outperformed the premium product in every test category." Language conveys surprise.', '["Uses surprise language (surprisingly, unexpectedly)", "Shows contrast with expectations", "Maintains appropriate tone", "Is clear and specific"]', 1, 'SAT Craft & Structure', 17, 'calc-recommended'),
('sat-craft-018', 'sat-craft-structure-general', 'Write a sentence that includes a counterargument to an opinion.', 'hard', 'Example: "While critics claim the program is too expensive, supporters argue that the long-term benefits far exceed the initial investment." This presents opposing view.', '["Presents opposing viewpoint", "Uses appropriate transition", "Is stated fairly", "Sets up balanced discussion"]', 1, 'SAT Craft & Structure', 18, 'calc-recommended'),
('sat-craft-019', 'sat-craft-structure-general', 'Write a sentence that includes a concession, such as "Although this may be true...".', 'hard', 'Example: "Although the technology has limitations, its potential to transform healthcare makes it worth pursuing." This acknowledges weakness while maintaining position.', '["Uses concession language (although, while)", "Acknowledges opposing point", "Maintains overall argument", "Shows balanced thinking"]', 1, 'SAT Craft & Structure', 19, 'calc-recommended'),
('sat-craft-020', 'sat-craft-structure-general', 'Write a sentence that clarifies information using a phrase like "In other words...".', 'medium', 'Example: "The process requires significant resources; in other words, it will be expensive and time-consuming." This restates for clarity.', '["Uses clarifying transition", "Restates idea more clearly", "Adds understanding", "Is grammatically correct"]', 1, 'SAT Craft & Structure', 20, 'calc-recommended'),
('sat-craft-021', 'sat-craft-structure-general', 'Write a sentence that shows the writer is evaluating or judging something.', 'medium', 'Example: "The documentary effectively presents multiple perspectives, though it occasionally oversimplifies complex economic issues." This provides evaluation.', '["Makes clear judgment", "Provides reasoning", "Uses evaluative language", "Is balanced and fair"]', 1, 'SAT Craft & Structure', 21, 'calc-recommended'),
('sat-craft-022', 'sat-craft-structure-general', 'Write a sentence that shows a cause and then a result.', 'medium', 'Example: "The drought reduced crop yields, resulting in higher food prices across the region." This clearly shows cause-effect relationship.', '["Shows clear cause", "Shows clear result", "Uses causal language (resulting in, leading to)", "Is logical"]', 1, 'SAT Craft & Structure', 22, 'calc-recommended'),
('sat-craft-023', 'sat-craft-structure-general', 'Write a sentence that clearly states the purpose of something.', 'easy', 'Example: "The purpose of this device is to monitor air quality and alert users to potentially harmful pollution levels." This explicitly states purpose.', '["Clearly states purpose", "Is specific and direct", "Uses purpose language (purpose is, designed to)", "Is complete"]', 1, 'SAT Craft & Structure', 23, 'calc-recommended'),
('sat-craft-024', 'sat-craft-structure-general', 'Write a sentence that gives a comparison using more/less or better/worse.', 'medium', 'Example: "The new model is more efficient than its predecessor, using 30% less energy while providing better performance." This makes clear comparison.', '["Uses comparative language", "Compares specific qualities", "Is clear and specific", "Provides meaningful comparison"]', 1, 'SAT Craft & Structure', 24, 'calc-recommended'),
('sat-craft-025', 'sat-craft-structure-general', 'Write a sentence that expresses a strong opinion clearly.', 'medium', 'Example: "It is absolutely essential that we prioritize renewable energy development to combat climate change." This states strong opinion clearly.', '["Expresses clear opinion", "Uses strong language appropriately", "Is direct and confident", "Maintains appropriate tone"]', 1, 'SAT Craft & Structure', 25, 'calc-recommended'),
('sat-craft-026', 'sat-craft-structure-general', 'Write a sentence that shows a change of perspective (example: "At first... Now...").', 'hard', 'Example: "At first, the theory seemed implausible; now, mounting evidence has convinced most scientists of its validity." This shows perspective shift.', '["Shows clear before/after", "Uses transition words (at first, now)", "Shows change in thinking", "Is logical and clear"]', 1, 'SAT Craft & Structure', 26, 'calc-recommended'),
('sat-craft-027', 'sat-craft-structure-general', 'Write a sentence that shows agreement with someone else''s idea.', 'easy', 'Example: "I agree with the proposal that public parks should be expanded, as they provide essential green space for communities." This shows clear agreement.', '["Expresses agreement clearly", "Uses agreement language", "Provides reasoning", "Is respectful and clear"]', 1, 'SAT Craft & Structure', 27, 'calc-recommended'),
('sat-craft-028', 'sat-craft-structure-general', 'Write a sentence that shows disagreement with someone else''s idea.', 'medium', 'Example: "I respectfully disagree with this approach, as it fails to address the root causes of the problem." This shows disagreement with reasoning.', '["Expresses disagreement clearly", "Maintains respectful tone", "Provides reasoning", "Is constructive"]', 1, 'SAT Craft & Structure', 28, 'calc-recommended'),
('sat-craft-029', 'sat-craft-structure-general', 'Write a sentence that could be used as a final concluding statement.', 'medium', 'Example: "Ultimately, the success of this initiative will depend on community engagement and sustained commitment from all stakeholders." This provides effective closure.', '["Provides sense of closure", "Summarizes or looks forward", "Uses conclusion language", "Is strong and memorable"]', 1, 'SAT Craft & Structure', 29, 'calc-recommended'),
('sat-craft-030', 'sat-craft-structure-general', 'Write a sentence that introduces a new idea or topic smoothly.', 'medium', 'Example: "Turning now to the economic implications, we must consider how these changes will affect local businesses." This transitions smoothly to new topic.', '["Uses transition phrase", "Introduces new topic clearly", "Connects to previous discussion", "Is smooth and natural"]', 1, 'SAT Craft & Structure', 30, 'calc-recommended');