-- Add 30 Standard English Conventions questions
INSERT INTO curriculum_questions (id, topic_id, question, difficulty, model_answer, marking_criteria, marks, spec_reference, order_index, calculator_guidance) VALUES
-- Easy (1-10)
('sat-eng-conv-001', 'sat-english-conventions-general', 'Write a sentence using correct grammar with the phrase: "Neither the students nor the teacher…"', 'easy', 'Neither the students nor the teacher was available for the meeting.', '["Uses correct subject-verb agreement with neither...nor", "Sentence is grammatically complete", "Meaning is clear and logical"]', 1, 'SAT English: Subject-Verb Agreement', 1, NULL),
('sat-eng-conv-002', 'sat-english-conventions-general', 'Write a sentence with three items in a list using commas correctly.', 'easy', 'I need to buy apples, oranges, and bananas from the store.', '["Uses commas correctly between list items", "Includes serial comma before final item", "Sentence is grammatically complete"]', 1, 'SAT English: Comma Usage', 2, NULL),
('sat-eng-conv-003', 'sat-english-conventions-general', 'Write a sentence using "each" and match it with a singular pronoun later.', 'easy', 'Each student must bring his or her own lunch to school.', '["Uses singular pronoun with each", "Maintains pronoun consistency", "Sentence is grammatically complete"]', 1, 'SAT English: Pronoun Agreement', 3, NULL),
('sat-eng-conv-004', 'sat-english-conventions-general', 'Write a sentence where the describing phrase is placed next to the thing it describes.', 'easy', 'Running quickly through the park, the dog chased after the ball.', '["Modifier is placed next to what it modifies", "No dangling or misplaced modifier", "Sentence meaning is clear"]', 1, 'SAT English: Modifier Placement', 4, NULL),
('sat-eng-conv-005', 'sat-english-conventions-general', 'Write a sentence where all items in a list use the same verb form.', 'easy', 'She enjoys reading, writing, and painting in her free time.', '["All verbs use parallel structure (same form)", "List maintains consistency", "Sentence is grammatically complete"]', 1, 'SAT English: Parallel Structure', 5, NULL),
('sat-eng-conv-006', 'sat-english-conventions-general', 'Write a sentence using only one verb tense (past or present — not mixed).', 'easy', 'Yesterday, I walked to the store and bought some groceries.', '["Uses consistent verb tense throughout", "No inappropriate tense shifts", "Sentence is grammatically complete"]', 1, 'SAT English: Verb Tense Consistency', 6, NULL),
('sat-eng-conv-007', 'sat-english-conventions-general', 'Write a sentence using the word "data" with a plural verb.', 'easy', 'The data suggest that temperatures are rising globally.', '["Uses data with plural verb", "Subject-verb agreement is correct", "Sentence is grammatically complete"]', 1, 'SAT English: Subject-Verb Agreement', 7, NULL),
('sat-eng-conv-008', 'sat-english-conventions-general', 'Write a sentence that starts with a short phrase and uses a comma after it.', 'easy', 'After dinner, we watched a movie together.', '["Introductory phrase followed by comma", "Comma placement is correct", "Sentence is grammatically complete"]', 1, 'SAT English: Comma Usage', 8, NULL),
('sat-eng-conv-009', 'sat-english-conventions-general', 'Write a sentence where a group noun (team/class/committee) uses a matching pronoun.', 'easy', 'The team celebrated its victory with enthusiasm.', '["Collective noun paired with singular pronoun", "Pronoun agreement is correct", "Sentence is grammatically complete"]', 1, 'SAT English: Pronoun Agreement', 9, NULL),
('sat-eng-conv-010', 'sat-english-conventions-general', 'Write a sentence where the word "only" is placed correctly so the meaning is clear.', 'easy', 'She only eats vegetables for lunch.', '["Only is placed to clarify intended meaning", "No ambiguity in sentence", "Sentence is grammatically complete"]', 1, 'SAT English: Modifier Placement', 10, NULL),
-- Medium (11-25)
('sat-eng-conv-011', 'sat-english-conventions-general', 'Write a sentence with a parallel structure in the verbs.', 'medium', 'The job requires answering phones, scheduling appointments, and managing files.', '["All verbs use parallel structure", "Consistency maintained throughout list", "Sentence is grammatically complete"]', 1, 'SAT English: Parallel Structure', 11, NULL),
('sat-eng-conv-012', 'sat-english-conventions-general', 'Write one sentence using the past perfect tense correctly (example: "had finished").', 'medium', 'By the time we arrived, the concert had already started.', '["Uses past perfect tense correctly", "Shows action completed before another past action", "Sentence is grammatically complete"]', 1, 'SAT English: Verb Tense', 12, NULL),
('sat-eng-conv-013', 'sat-english-conventions-general', 'Write a sentence with a subject joined by "or" and match the verb correctly.', 'medium', 'Either the manager or the employees are responsible for closing the store.', '["Verb agrees with nearest subject in or construction", "Subject-verb agreement is correct", "Sentence is grammatically complete"]', 1, 'SAT English: Subject-Verb Agreement', 13, NULL),
('sat-eng-conv-014', 'sat-english-conventions-general', 'Write a sentence with a phrase in the middle that uses commas around it.', 'medium', 'My brother, who lives in Boston, is visiting next week.', '["Nonessential clause set off with commas", "Comma placement is correct", "Sentence is grammatically complete"]', 1, 'SAT English: Comma Usage', 14, NULL),
('sat-eng-conv-015', 'sat-english-conventions-general', 'Write a sentence comparing two things using the correct pronoun (than me / than I).', 'medium', 'She is taller than I am.', '["Uses correct pronoun case in comparison", "Comparison is grammatically correct", "Sentence is grammatically complete"]', 1, 'SAT English: Pronoun Case', 15, NULL),
('sat-eng-conv-016', 'sat-english-conventions-general', 'Start a sentence with a phrase like "After studying…" and make sure the sentence makes sense.', 'medium', 'After studying for hours, I finally understood the concept.', '["Introductory phrase modifies correct subject", "No dangling modifier", "Sentence is grammatically complete"]', 1, 'SAT English: Modifier Placement', 16, NULL),
('sat-eng-conv-017', 'sat-english-conventions-general', 'Write a sentence that uses both infinitives and gerunds but keeps them parallel.', 'medium', 'I love to read and to write in my free time.', '["Maintains parallel structure with infinitives or gerunds", "Consistency in verb forms", "Sentence is grammatically complete"]', 1, 'SAT English: Parallel Structure', 17, NULL),
('sat-eng-conv-018', 'sat-english-conventions-general', 'Write a sentence using a semicolon correctly between two complete ideas.', 'medium', 'The weather was perfect; we decided to go hiking.', '["Semicolon joins two independent clauses", "Both clauses are complete sentences", "Punctuation is used correctly"]', 1, 'SAT English: Semicolon Usage', 18, NULL),
('sat-eng-conv-019', 'sat-english-conventions-general', 'Write a sentence using a reflexive pronoun correctly (myself/herself/themselves).', 'medium', 'She prepared herself for the challenging interview.', '["Reflexive pronoun used correctly", "Pronoun refers back to subject", "Sentence is grammatically complete"]', 1, 'SAT English: Pronoun Usage', 19, NULL),
('sat-eng-conv-020', 'sat-english-conventions-general', 'Write a sentence showing consistent verb form while describing progress.', 'medium', 'The students are learning, growing, and achieving their goals.', '["All verbs maintain consistent form", "Parallel structure throughout", "Sentence is grammatically complete"]', 1, 'SAT English: Parallel Structure', 20, NULL),
('sat-eng-conv-021', 'sat-english-conventions-general', 'Write a sentence using the word "team" with the correct verb (is or are, depending on meaning).', 'medium', 'The team is competing in the finals this weekend.', '["Collective noun paired with appropriate verb", "Subject-verb agreement matches intended meaning", "Sentence is grammatically complete"]', 1, 'SAT English: Subject-Verb Agreement', 21, NULL),
('sat-eng-conv-022', 'sat-english-conventions-general', 'Write a sentence with a plural possessive noun (example: students'' backpacks).', 'medium', 'The students'' projects were displayed in the hallway.', '["Uses correct plural possessive form", "Apostrophe placement is correct", "Sentence is grammatically complete"]', 1, 'SAT English: Apostrophe Usage', 22, NULL),
('sat-eng-conv-023', 'sat-english-conventions-general', 'Write a sentence where the pronoun stays singular or plural — not both.', 'medium', 'Each person must take responsibility for their own actions.', '["Maintains pronoun consistency", "Avoids shifting between singular and plural", "Sentence is grammatically complete"]', 1, 'SAT English: Pronoun Consistency', 23, NULL),
('sat-eng-conv-024', 'sat-english-conventions-general', 'Write a sentence where the describing phrase is in the correct spot and not confusing.', 'medium', 'Covered in snow, the mountain looked beautiful in the morning light.', '["Modifier clearly modifies intended noun", "No misplaced or dangling modifier", "Sentence meaning is clear"]', 1, 'SAT English: Modifier Placement', 24, NULL),
('sat-eng-conv-025', 'sat-english-conventions-general', 'Write a sentence comparing two things using as…as, more than, or less than.', 'medium', 'This book is more interesting than the one I read last week.', '["Uses correct comparative structure", "Comparison is grammatically sound", "Sentence is grammatically complete"]', 1, 'SAT English: Comparatives', 25, NULL),
-- Hard (26-30)
('sat-eng-conv-026', 'sat-english-conventions-general', 'Write a sentence giving advice using the correct verb form (example: I suggest that he study).', 'hard', 'The teacher recommends that each student complete the assignment independently.', '["Uses subjunctive mood correctly", "Verb form matches requirement structure", "Sentence is grammatically complete"]', 1, 'SAT English: Subjunctive Mood', 26, NULL),
('sat-eng-conv-027', 'sat-english-conventions-general', 'Write a sentence joining two complete ideas without a comma splice.', 'hard', 'The experiment failed; however, we learned valuable lessons from it.', '["Avoids comma splice error", "Uses appropriate punctuation or conjunction", "Both clauses are independent"]', 1, 'SAT English: Run-on Sentences', 27, NULL),
('sat-eng-conv-028', 'sat-english-conventions-general', 'Write a sentence with a hyphenated adjective before a noun (example: full-time job).', 'hard', 'She applied for a well-paying position at the company.', '["Compound adjective is correctly hyphenated", "Hyphen usage follows standard conventions", "Sentence is grammatically complete"]', 1, 'SAT English: Hyphen Usage', 28, NULL),
('sat-eng-conv-029', 'sat-english-conventions-general', 'Write a sentence with elliptical parallelism (leaving out repeated words because they''re understood).', 'hard', 'Some students prefer morning classes, others afternoon sessions.', '["Uses elliptical construction correctly", "Omitted words are clearly understood", "Parallel structure maintained"]', 1, 'SAT English: Ellipsis and Parallelism', 29, NULL),
('sat-eng-conv-030', 'sat-english-conventions-general', 'Write a sentence using the correct comparative or superlative adjective (example: better/best).', 'hard', 'Of all the candidates, she performed the best during the interview.', '["Uses correct superlative form", "Comparison is appropriate for context", "Sentence is grammatically complete"]', 1, 'SAT English: Comparatives and Superlatives', 30, NULL);

-- Add 30 Expression of Ideas questions
INSERT INTO curriculum_questions (id, topic_id, question, difficulty, model_answer, marking_criteria, marks, spec_reference, order_index, calculator_guidance) VALUES
-- Easy (1-10)
('sat-exp-ideas-001', 'sat-expression-ideas-general', 'Write one sentence supporting the idea of healthier school lunches.', 'easy', 'Healthier school lunches can improve students'' concentration and energy levels throughout the day.', '["Provides clear support for the claim", "Sentence is focused and relevant", "Reasoning is logical and appropriate"]', 1, 'SAT Writing: Supporting Claims', 1, NULL),
('sat-exp-ideas-002', 'sat-expression-ideas-general', 'Write one sentence supporting the idea of better public transport.', 'easy', 'Better public transport reduces traffic congestion and helps protect the environment.', '["Provides clear support for the claim", "Sentence is focused and relevant", "Reasoning is logical and appropriate"]', 1, 'SAT Writing: Supporting Claims', 2, NULL),
('sat-exp-ideas-003', 'sat-expression-ideas-general', 'Write one sentence supporting the idea of longer school holidays.', 'easy', 'Longer school holidays give students more time to rest and pursue their personal interests.', '["Provides clear support for the claim", "Sentence is focused and relevant", "Reasoning is logical and appropriate"]', 1, 'SAT Writing: Supporting Claims', 3, NULL),
('sat-exp-ideas-004', 'sat-expression-ideas-general', 'Write one sentence supporting the idea of community volunteering.', 'easy', 'Community volunteering helps develop empathy and builds stronger connections between neighbors.', '["Provides clear support for the claim", "Sentence is focused and relevant", "Reasoning is logical and appropriate"]', 1, 'SAT Writing: Supporting Claims', 4, NULL),
('sat-exp-ideas-005', 'sat-expression-ideas-general', 'Write one sentence supporting the idea of school clubs and activities.', 'easy', 'School clubs and activities help students discover new talents and make lasting friendships.', '["Provides clear support for the claim", "Sentence is focused and relevant", "Reasoning is logical and appropriate"]', 1, 'SAT Writing: Supporting Claims', 5, NULL),
('sat-exp-ideas-006', 'sat-expression-ideas-general', 'Write one sentence supporting the idea of less homework.', 'easy', 'Less homework allows students more time for family activities and adequate sleep.', '["Provides clear support for the claim", "Sentence is focused and relevant", "Reasoning is logical and appropriate"]', 1, 'SAT Writing: Supporting Claims', 6, NULL),
('sat-exp-ideas-007', 'sat-expression-ideas-general', 'Write a sentence that argues against one of the statements you wrote above.', 'easy', 'However, reducing homework too much could leave students unprepared for college-level work.', '["Presents a counterargument effectively", "Sentence is focused and relevant", "Reasoning is logical and appropriate"]', 1, 'SAT Writing: Counterarguments', 7, NULL),
('sat-exp-ideas-008', 'sat-expression-ideas-general', 'Write a sentence that introduces a new idea using a transition word (for example: also, next, finally).', 'easy', 'Additionally, schools should consider implementing mental health support programs.', '["Uses appropriate transition word", "Introduces new idea clearly", "Sentence flows logically"]', 1, 'SAT Writing: Transitions', 8, NULL),
('sat-exp-ideas-009', 'sat-expression-ideas-general', 'Write a sentence giving a clear reason for an opinion.', 'easy', 'Students should have access to technology in classrooms because it prepares them for modern careers.', '["Provides clear reasoning", "Opinion is supported logically", "Sentence is focused and complete"]', 1, 'SAT Writing: Reasoning', 9, NULL),
('sat-exp-ideas-010', 'sat-expression-ideas-general', 'Write a sentence giving a specific example to support a claim.', 'easy', 'For instance, students who participate in sports often develop better time management skills.', '["Provides specific, relevant example", "Example supports the claim effectively", "Sentence is clear and focused"]', 1, 'SAT Writing: Examples', 10, NULL),
-- Medium (11-25)
('sat-exp-ideas-011', 'sat-expression-ideas-general', 'Write a sentence using a number or fact to strengthen an argument.', 'medium', 'Studies show that 85% of employers value teamwork skills above technical knowledge.', '["Uses specific data or statistics", "Fact strengthens the argument", "Sentence is credible and focused"]', 1, 'SAT Writing: Evidence', 11, NULL),
('sat-exp-ideas-012', 'sat-expression-ideas-general', 'Write a sentence using a rhetorical question to make a point.', 'medium', 'If we don''t invest in renewable energy now, what kind of planet will future generations inherit?', '["Uses rhetorical question effectively", "Question emphasizes the point", "Sentence is persuasive and clear"]', 1, 'SAT Writing: Rhetorical Devices', 12, NULL),
('sat-exp-ideas-013', 'sat-expression-ideas-general', 'Write a sentence that states a clear argument or claim.', 'medium', 'Schools must prioritize mental health education to address the growing crisis among teenagers.', '["States clear, focused claim", "Argument is specific and debatable", "Sentence is assertive and direct"]', 1, 'SAT Writing: Claims', 13, NULL),
('sat-exp-ideas-014', 'sat-expression-ideas-general', 'Write a sentence that adds a supporting detail to a claim.', 'medium', 'This approach not only reduces stress but also improves academic performance significantly.', '["Adds relevant supporting detail", "Detail strengthens the main claim", "Sentence connects logically"]', 1, 'SAT Writing: Supporting Details', 14, NULL),
('sat-exp-ideas-015', 'sat-expression-ideas-general', 'Write a sentence that gives a short background or introduction to a topic.', 'medium', 'In recent years, climate change has become one of the most pressing issues facing humanity.', '["Provides appropriate context", "Introduction sets up topic clearly", "Sentence is broad enough to introduce"]', 1, 'SAT Writing: Topic Introduction', 15, NULL),
('sat-exp-ideas-016', 'sat-expression-ideas-general', 'Write a sentence that explains one benefit of an idea.', 'medium', 'One major advantage of remote learning is the flexibility it offers to students with different schedules.', '["Identifies specific benefit", "Explanation is clear and relevant", "Sentence is focused on advantage"]', 1, 'SAT Writing: Benefits', 16, NULL),
('sat-exp-ideas-017', 'sat-expression-ideas-general', 'Write a sentence that explains one disadvantage of an idea.', 'medium', 'On the other hand, remote learning can reduce opportunities for social interaction and collaboration.', '["Identifies specific disadvantage", "Explanation is clear and relevant", "Sentence is focused on drawback"]', 1, 'SAT Writing: Drawbacks', 17, NULL),
('sat-exp-ideas-018', 'sat-expression-ideas-general', 'Write a sentence making a recommendation (example: schools should…).', 'medium', 'Schools should implement mandatory financial literacy courses to prepare students for adult responsibilities.', '["Makes clear, actionable recommendation", "Suggestion is specific and purposeful", "Sentence is direct and persuasive"]', 1, 'SAT Writing: Recommendations', 18, NULL),
('sat-exp-ideas-019', 'sat-expression-ideas-general', 'Write a sentence making a prediction about the future.', 'medium', 'Within the next decade, artificial intelligence will likely transform nearly every industry.', '["Makes reasonable prediction", "Future statement is clear", "Sentence is forward-looking"]', 1, 'SAT Writing: Predictions', 19, NULL),
('sat-exp-ideas-020', 'sat-expression-ideas-general', 'Write a sentence giving a warning about something.', 'medium', 'If we continue ignoring mental health concerns, we risk creating a generation of overwhelmed students.', '["Presents clear warning", "Consequence is identified", "Sentence is cautionary and impactful"]', 1, 'SAT Writing: Warnings', 20, NULL),
('sat-exp-ideas-021', 'sat-expression-ideas-general', 'Write a sentence starting with a contrast transition (however/although/on the other hand).', 'medium', 'However, critics argue that standardized testing fails to measure true understanding or creativity.', '["Uses contrast transition appropriately", "Introduces contrasting idea", "Sentence flows logically"]', 1, 'SAT Writing: Contrast Transitions', 21, NULL),
('sat-exp-ideas-022', 'sat-expression-ideas-general', 'Write a sentence starting with an addition transition (also/in addition/furthermore).', 'medium', 'Furthermore, implementing these changes would require significant financial investment from schools.', '["Uses addition transition appropriately", "Adds to previous point", "Sentence flows logically"]', 1, 'SAT Writing: Addition Transitions', 22, NULL),
('sat-exp-ideas-023', 'sat-expression-ideas-general', 'Write a sentence starting with a cause-and-effect transition (therefore/so/as a result).', 'medium', 'Therefore, students who develop strong reading habits early tend to excel in all academic subjects.', '["Uses cause-effect transition appropriately", "Shows logical consequence", "Sentence demonstrates clear connection"]', 1, 'SAT Writing: Cause-Effect Transitions', 23, NULL),
('sat-exp-ideas-024', 'sat-expression-ideas-general', 'Write a sentence starting with a conclusion transition (overall/in conclusion/to sum up).', 'medium', 'In conclusion, investing in education yields benefits that extend far beyond individual success.', '["Uses conclusion transition appropriately", "Summarizes or concludes effectively", "Sentence provides closure"]', 1, 'SAT Writing: Conclusion Transitions', 24, NULL),
('sat-exp-ideas-025', 'sat-expression-ideas-general', 'Write a sentence giving an example starting with "For example,".', 'medium', 'For example, Finland''s education system emphasizes play-based learning in early childhood.', '["Introduces example with appropriate transition", "Example is specific and relevant", "Sentence supports broader point"]', 1, 'SAT Writing: Examples', 25, NULL),
-- Hard (26-30)
('sat-exp-ideas-026', 'sat-expression-ideas-general', 'Write a sentence mentioning both sides of an argument.', 'hard', 'While technology in classrooms can enhance learning, it may also serve as a distraction if not properly managed.', '["Presents both perspectives fairly", "Balance is maintained", "Sentence acknowledges complexity"]', 1, 'SAT Writing: Balanced Arguments', 26, NULL),
('sat-exp-ideas-027', 'sat-expression-ideas-general', 'Write one sentence that stays completely focused on one idea.', 'hard', 'Effective time management skills enable students to balance academics, extracurriculars, and personal well-being.', '["Maintains single, clear focus", "No tangential information", "Sentence is unified and coherent"]', 1, 'SAT Writing: Unity', 27, NULL),
('sat-exp-ideas-028', 'sat-expression-ideas-general', 'Write a sentence explaining why staying on topic is important.', 'hard', 'Staying on topic ensures that arguments remain coherent and persuasive, preventing confusion for readers.', '["Explains importance clearly", "Reasoning is logical", "Sentence demonstrates understanding"]', 1, 'SAT Writing: Coherence', 28, NULL),
('sat-exp-ideas-029', 'sat-expression-ideas-general', 'Write a sentence that could be used as the first sentence of a paragraph.', 'hard', 'The benefits of bilingual education extend far beyond simply learning another language.', '["Functions as effective topic sentence", "Sets up paragraph focus", "Sentence is broad enough to develop"]', 1, 'SAT Writing: Topic Sentences', 29, NULL),
('sat-exp-ideas-030', 'sat-expression-ideas-general', 'Write a sentence that could be used as the last sentence of a paragraph.', 'hard', 'Ultimately, these strategies demonstrate how thoughtful planning can lead to lasting academic success.', '["Functions as effective concluding sentence", "Provides closure to paragraph", "Sentence synthesizes main points"]', 1, 'SAT Writing: Concluding Sentences', 30, NULL);